mapper.select:
SELECT COUNT(*) FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD SET
BOARD_COUNT = BOARD_COUNT + 1
WHERE BOARD_NO = ${boardNo}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE BOARD_NO = ${boardNo}
AND MEMBER_NO = ${memberNo}

mapper.update:
UPDATE BOARD_IMG SET
    IMG_PATH   = #{imgPath},
    IMG_RENAME = #{imgRename}
WHERE BOARD_NO = #{boardNo}
